CustomField.formula:
IF ( ROUND( (LastModifiedDate - CreatedDate)*24*60, 0 ) < 60, TEXT(ROUND((LastModifiedDate - CreatedDate)*24*60, 0)) & " minute(s)",
  IF ( ROUND( (LastModifiedDate - CreatedDate)*24, 0 ) < 24, TEXT(ROUND((LastModifiedDate - CreatedDate)*24, 0)) & " hour(s)",
    TEXT(ROUND(LastModifiedDate - CreatedDate, 0)) & " day(s)"
  )
)
